$_MODULE['<{innovativemenu}blf>get_tabs_of_menu_f2a6c498fb90ee345d997f888fce3b18'] = 'Supprimer';
$_MODULE['<{innovativemenu}blf>manage_fonts_2ece379fe1cf945bafb268dd6130c621'] = 'Liste des familles de caractères';
$_MODULE['<{innovativemenu}blf>manage_fonts_9efa60347336819172fa315ba9698718'] = 'Créer, éditer et effacer vos polices pouvant être utilisées pour votre menu.';
$_MODULE['<{innovativemenu}blf>manage_fonts_a83ab6f58c6b9b27fe1b6e7a730ba54d'] = 'Ces polices peuvent être existantes (Arial, Calibri, Times) ou personnalisées avec un fichier .zip contenant des fichiers ttf, otf, eot ou woff.';
$_MODULE['<{innovativemenu}blf>manage_fonts_eacc2b77741792b0efcf4e787d592a99'] = 'Il est recommandé de mettre des alternatives à vos polices';
$_MODULE['<{innovativemenu}blf>manage_links_e30f24c4514258abd8f9a8e763e6ff13'] = 'Liste des liens';
$_MODULE['<{innovativemenu}blf>manage_links_d020c0eb0bb0bdfaf6d9677d28f82ac3'] = 'Créez ici vos liens personnalisés (Par exemple page d\'accueil, blog,...). Ils peuvent par exemple vous permettre de créer un onglet qui renvoie sur un blog ou site partenaire (le contenu est forcément différent des pages cms, transporteurs, fournisseurs ou catégories).';
$_MODULE['<{innovativemenu}blf>manage_menus_3bb866d5c3f19c02377515b4e7a56d00'] = 'Liste des menus créés';
$_MODULE['<{innovativemenu}blf>user_guide_e5e976f2e2cb6599b2de3cdd08c24700'] = 'Menu : élément composé d\'une barre de menu et d\'un ensemble d\'onglets.';
$_MODULE['<{innovativemenu}blf>user_guide_fda67441bae703b519121d44c7d98a2c'] = 'Barre de menu : graphisme du menu ayant une taille, des couleurs et une police de caractères';
$_MODULE['<{innovativemenu}blf>user_guide_7159c9fc7b74553c30e9b790244569d1'] = 'Onglet : contenu du menu qui peut être une catégorie, un cms, un fabricant, un fournisseur ou un lien. 	Chaque onglet est lié à un bloc qui contient des colonnes et des publicités.';
$_MODULE['<{innovativemenu}blf>user_guide_36e9321da0825ca6daaf37732b4db71b'] = 'Les colonnes sont :';
$_MODULE['<{innovativemenu}blf>user_guide_4aaa9b6b4e3121ce57bd9d9107bb85a3'] = 'Des listes dont les lignes dépendent du type de l\'onglet. Ainsi, un onglet lié à la catégorie A ne contiendra que des listes de produits de catégorie A, ou des sous catégories de A ou encore des liens personnalisés.';
$_MODULE['<{innovativemenu}blf>user_guide_9dffbf69ffba8bc38bc4e01abf4b1675'] = 'Texte';
$_MODULE['<{innovativemenu}blf>user_guide_2a8474e7641a52e5d08dd4622fff96eb'] = 'Les publicités peuvent être alignées à gauche, à droite, en haut ou en bas. Elles sont personnalisables avec du texte et des images.';
$_MODULE['<{innovativemenu}blf>user_guide_c91734fe43bbe3ffaf70060f5dfa6d25'] = 'Créer/éditer un menu.';
$_MODULE['<{innovativemenu}blf>user_guide_a3e1e0b9d7942f071e3a8491ba064bef'] = 'Editer le nom du menu (pour le retrouver plus facilement dans la liste des menus créés)';
$_MODULE['<{innovativemenu}blf>user_guide_71fafe7a6a4a7b42309225137cf82e4f'] = 'Editer la barre de menu en choisissant la taille, les couleurs, les polices de caractères puis les arrondis.';
$_MODULE['<{innovativemenu}blf>user_guide_91cfe1aa9b726c97e77a013ec0ae2a91'] = 'Editer ensuite les caractéristiques de chaque onglet.';
$_MODULE['<{innovativemenu}blf>user_guide_ffcef79e440773b5ff016e2308f95b17'] = 'Si la configuration générale du menu est activée, tous les onglets auront le style général de la barre de menu. Autrement, éditer les couleurs et les polices de l\'onglet, du bloc et des colonnes.';
$_MODULE['<{innovativemenu}blf>user_guide_7dc9730ba24860b06856518e4ac49f8c'] = 'Choisir le type de l\'onglet puis ses colonnes et ses publicités.';
$_MODULE['<{innovativemenu}blf>user_guide_b9b0e33f1f990f85d7315260debcf4df'] = 'Dans un menu, l\'ordre d\'affichage des onglets peut être modifié en cliquant sur la flèche haute ou la flèche basse. De même, dans chaque onglet, l\'ordre des colonnes et des publicités peut être modifiée.';
$_MODULE['<{innovativemenu}blf>user_guide_ef2e3291bac82426e36e5d7439b68a1e'] = 'Durant toute la création du menu, un aperçu est proposé. C\'est cet aperçu qui sera être affiché en front office. Il est possible de créer autant de menus que désiré. Un seul sera affiché; celui qui est actif. Ainsi, activer un menu désactive tous les autres.';
